sys_script.script:
(function executeRule(current, previous /*null when async*/) {
  var cost = current.getValue('cost')
  const gr = new GlideRecord('x_yunji_oa_t_trip_standard');
    const grca = new GlideRecord('x_yunji_oa_t_trip_cost_approve');
    grca.addQuery('sys_id',current.getValue('quote'));
    grca.query();
    if(grca.hasNext()){
    	grca.next();
      gr.addQuery('department',grca.getValue('department'));
//    	gr.addQuery('project',grca.getValue('project'));
    	gr.addQuery('company',grca.getValue('company'));
    }
    gr.addQuery('costType',current.getValue('costType'));
    gr.addQuery('positionLevel','Y5');
    gr.query();
    if(gr.hasNext()){
    	gr.next();
      var maxCost = gr.getValue('maxCost');
      if(cost>maxCost){
        gs.addErrorMessage("您的报销的金额超过上限，为您调整为上限金额，超出部分无法报销");
        current.setValue('cost',maxCost);
      }else{
        current.setValue('cost',cost);
      }
    }else{
    	current.setValue('cost',cost);
    }
})(current, previous);
