sys_script_include.script:
var CostStandardUtils = Class.create();
CostStandardUtils.prototype = Object.extendsObject(AbstractAjaxProcessor,{
  checkInvoice: function(){
    var invoice = this.getParameter('invoice');
    var gr = new GlideRecord('x_yunji_jet_14_t_cost');
    gr.addQuery('invoice',invoice);
    gr.query();
    if(gr.hasNext()){
    	gr.next();
      this.addAttribute("result", '');
    }else{
    	this.addAttribute("result", invoice);
    }
  },
  checkCost: function(){
    var approveId = this.getParameter('approveId');
    var costType = this.getParameter('costType');
    var level = null;
    var cost = this.getParameter('cost');
  	var gr = new GlideRecord('x_yunji_jet_14_t_trip_standard');

    var grca = new GlideRecord('x_yunji_jet_14_t_trip_cost_approve');
    grca.addQuery('sys_id',approveId);
    grca.query();
    if(grca.hasNext()){
    	grca.next();
      var palnId = grca.getValue('tripPlanName',)
      var grtp = new GlideRecord('x_yunji_jet_14_t_trip_plan');
      grtp.addQuery('sys_id',palnId);
      grtp.query();
      if(grtp.hasNext()){
      	grtp.next();
        level = grtp.getValue('positionLevel');
      }
    }

    gr.addQuery('costType',costType);
    gr.addQuery('positionLevel',level);
    //gr.addQuery('positionLevel','Y5');

    gr.query();
    if(gr.hasNext()){

    	gr.next();
      var maxCost = gr.getValue('maxCost');
      if(cost>maxCost){
       gs.addErrorMessage("您的报销的金额超过上限，为您调整为上限金额，超出部分无法报销");
        this.addAttribute("result", maxCost);
      }else{
        this.addAttribute("result", cost);
      }
    }else{
    	this.addAttribute("result", cost);
    }  
  }
});
