sys_variable_value.value:
var department = '';
var tripPlanName = '';
var project = '';
var sysId = '';
var totalAmount = 0;
var tripNum = 0;
var totalDuration = 0;
var amount = 0;
var gr = new GlideRecord("x_yunji_oa_t_trip_cost_approve");
gr.orderByDesc('sys_created_on');
gr.setLimit(1);
gr.query();
if (gr.next() != null){
     amount = gr.getValue("amount"); 
     sysId = gr.getValue("tripPlanName");
     gs.print('sysId  ' + sysId);
     gs.print('amount ' + amount);
}

    var gr1 = new GlideRecord("x_yunji_oa_t_trip_plan");
    gr1.addQuery("sysId", sysId);
    gr1.query();
    if(gr1.next() != null){
        department = gr1.getValue('department');
        duration = gr1.getValue("duration");
        gs.print('duration ' + duration);
        gs.print('department ' + department);
    }

    var gr2 = new GlideRecord("x_yunji_oa_t_trip_project_budget");
    gr2.addQuery("department", department);
    gr2.query();

    if (gr2.next() != null){
        //gs.print(gr2.getValue('department'))
        // 计算累计开销金额
        totalAmount = gr2.getValue("totalAmount");
        gr2.setValue("totalAmount", totalAmount + amount);
        // 计算累计出差次数
        tripNum = gr2.getValue("tripNum");
        gr2.setValue("tripNum", tripNum + 1);
        // 计算累计出差时长
        totalDuration = gr2.getValue("totalDuration");
        gr2.setValue("totalDuration", totalDuration + duration);
        gr2.update();
  }
