sys_variable_value.value:
// 初始化字段
var name = 0;
var amount = 0;
var tripPlanName = '';
var sysId = '';

var gr = new GlideRecord("x_yunji_oa__t_trip_cost_approve");
gr.orderByDesc('sys_created_on');
gr.setLimit(1);
gr.query();
if (gr.next() != null){
     amount = gr.getValue("amount"); 
     sysId = gr.getValue("tripPlanName");
     gs.print('sysId  ' + sysId);
     gs.print('amount ' + amount);
}

var gr2 = new GlideRecord("x_yunji_oa_t_trip_plan");
gr2.addQuery("sysId", sysId);
gr2.query();
if (gr2.next() != null){
     name = gr2.getValue("name");
     gs.print('name ' + name);
}

var gr1 = new GlideRecord("x_yunji_oa_t_trip_petty_cash");
gr1.addQuery("name", name);
gr1.query();

if (gr1.next() != null){
     var num = gr1.getValue("num");
     gs.print('num ' + num);
     gr1.setValue("num", num - amount);
     gr1.update();
}
